def mask2idxs(mask):
    "Convert bool mask or index list to index `L`"
    if isinstance(mask,slice): return mask
    mask = list(mask)
    if len(mask)==0: return []
    if isinstance(mask[0],(bool,NoneType)): return [i for i,m in enumerate(mask) if m]
    return [int(i) for i in mask]

