def default_show_batch(x, y, its, ctxs=None, max_n=10, **kwargs):
    if ctxs is None: ctxs = Inf.nones
    for i in range(1 if y is None else 2):
        ctxs = [b.show(ctx=c, **kwargs) for b,c,_ in zip(its.itemgot(i),ctxs,range(max_n))]
    return ctxs

